import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component{

  constructor(props){
    super(props);
    this.state = {
      data : [],
      email : "",
      countries : []
    }
  }

  componentDidMount(){
      fetch('http://localhost:8080/data')
        .then(res => res.json())
        .then(json => {
            this.setState({
              data : json
            })
        });
  }
  handleEmailChange(e){
    this.setState({email:e.target.value});
    console.log(e.target.vale);
  }

  render(){

    var {data} = this.state;
    var count =0;
    let totalCases=0;
    let totalDeaths=0;
    let totalRecovered=0;



    data.map(d=> {
        totalCases = totalCases + d.confirmed;
        totalDeaths = totalDeaths + d.death;
        totalRecovered = totalRecovered +d.recovered; 


        }
      )

      let deathPercenteage = ((totalDeaths/totalCases)*100).toFixed(2);
      let recoverPercenteage = ((totalRecovered/totalCases)*100).toFixed(2);
    return(
      <div className ="App">
        <nav class="navbar navbar-light bg-light">
          <span class="navbar-brand mb-0 h1">Corona Data</span><br/>
          <span class="navbar-brand mb-0 h1">Total cases : {totalCases} | Total Deaths : {totalDeaths} with {deathPercenteage} %|Total Recovered : {totalRecovered} with {recoverPercenteage} %</span>
          <span class="navbar-brand mb-0 h1">You can subscribe with your email to get alerts of speicific country on your choice</span>
          <form class="input-group">
          <span class="navbar-brand mb-0 h1">
              Enter email : <input type="email" name = "email" placeholder="Email"/>
              <select>
                {data.map((d) => <option key={d.country1} value={d.country1}>{d.country1}</option>)}
              </select>
              <input type="submit" value = "Subscribe"/> 
          </span> 
          </form>
        </nav>




        <table class="table table-striped table-dark">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Country</th>
              <th scope="col">Active Cases</th>
              <th scope="col">Confirmed</th>
              <th scope="col">Death</th>
              <th scope="col">Death %</th>
              <th scope="col">Recovered</th>
              <th scope="col">Recovered %</th>
            </tr>
          </thead>
          <tbody>
          {data.map(d=>(
            <tr key={d.country1}>

          <td>{count+=1}</td>
              <td>{d.country1}</td>
              <td>{d.confirmed - (d.death + d.recovered)}</td>
              <td>{d.confirmed}</td>
              <td>{d.death} </td>
              <td>{((d.death/d.confirmed)*100).toFixed(2)}%</td>
              <td>{d.recovered}</td>
              <td>{((d.recovered/d.confirmed)*100).toFixed(2)}%</td>
            </tr>
          ))}
          </tbody>
        </table>
       </div>
    )
  }
}
export default App;
